/* Adding new tables to migrate properties from files to database */

DROP TABLE IF EXISTS `databuck_property_details`;
DROP TABLE IF EXISTS `databuck_properties_master`;

create table if not exists databuck_properties_master (
property_category_id int not null auto_increment,
property_category_name varchar(1000) not null UNIQUE,
description text,
created_at datetime,
PRIMARY KEY (property_category_id)
) engine=innodb auto_increment=1 default charset=latin1;

create table if not exists databuck_property_details (
property_id int not null auto_increment,
property_category_id int not null,
property_name varchar(2500) not null UNIQUE,
property_value text,
description text,
is_mandatory_field varchar(10) default 'N',
is_password_field varchar(10) default 'N',
is_value_encrypted varchar(10) default 'N',
property_default_value text,
property_data_type varchar(500),
prop_requires_restart varchar(10) default 'N',
last_updated_at datetime,
PRIMARY KEY (property_id),
FOREIGN KEY (property_category_id)
 	REFERENCES databuck_properties_master (property_category_id)
) engine=innodb auto_increment=1 default charset=latin1;


